import React, {useState} from 'react';
import {
  View,
  TouchableOpacity,
  Text,
  StyleSheet,
  Image,
  Alert,
} from 'react-native';
import ImagePicker from 'react-native-image-picker';
import api from './src/services/api';
export default function App() {
  const [avatar, setAvatar] = useState(null);
  const imagePickerOptions = {
    title: 'Selecione uma imagem',
  };
  function ImagePickerCallback(data) {
    console.log(data);
    if (data.didCancel) {
      return;
    }

    if (data.error) {
      return;
    }

    if (!data.uri) {
      return;
    }

    setAvatar(data);
  }

  async function uploadImage() {
    const data = new FormData();

    data.append('file', {
      originalname: avatar.fileName,
    });
    try {
      await api.post('/posts', data);
      Alert.alert('Upload realizado com sucesso!!');
    } catch (err) {
      Alert.alert('Ocorreu um erro ao fazer upload');
      console.log(err);
    }
  }
  return (
    <View style={styles.container}>
      <Image
        style={styles.avatar}
        source={{
          uri: avatar
            ? avatar.uri
            : 'data:image/png;base64,',
        }}
      />
      <TouchableOpacity
        style={styles.button}
        //A função showImagePicker recebe como parâmetros uma configuração
        //E uma função de callback onde podemos colocar função que salva no BD
        onPress={() =>
          ImagePicker.showImagePicker(imagePickerOptions, ImagePickerCallback)
        }>
        <Text style={styles.buttonText}>Escolher imagem</Text>
      </TouchableOpacity>

      <TouchableOpacity
        style={styles.button}
        //A função showImagePicker recebe como parâmetros uma configuração
        //E uma função de callback onde podemos colocar função que salva no BD
        onPress={uploadImage}>
        <Text style={styles.buttonText}>Enviar imagem</Text>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  button: {
    width: 150,
    height: 50,
    borderRadius: 4,
    backgroundColor: '#7159c1',
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: 20,
  },
  buttonText: {
    color: '#fff',
  },
  avatar: {
    width: 100,
    height: 100,
    borderRadius: 50,
  },
});
